    public void paintComponent(Graphics g) {
      super.paintComponent(g);

      int width = (int)(Math.min(getWidth(), getHeight()) * 0.4);
      int centerx = getWidth() / 2;
      int centery = getHeight() / 2;

      displaySquares(g, width, centerx, centery);
